import React, { Component } from 'react';
import './Query.css';

class Query extends Component {
    state = {
        word: "",
        results: [
            {
                id: 0,
                title: "시발",
                address: "병신아"
            },
            {
                id: 1,
                title: "장애인",
                address: "병신아2"
            }
        ]

    }

    componentDidMount() {
        this.UpdateQuery();
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.props.keyword !== prevProps.keyword) {
            this.UpdateQuery();
        }
    }

    UpdateQuery = () => {
        this.getQuery()
            .then(res => {
                this.setState({
                    word: res.query
                })
            })
            .catch(err => console.log(err));
    }

    getQuery = async () => {
        const response = await fetch('/api/query?query=' + this.props.keyword);
        const body = await response.json();

        console.log(body);
        return body;
    }

    render() {
        return (
            <div className="query-contatiner">
                {
                    this.state.results.map(q => {
                        <QueryResult
                        title={q.title}
                        address={q.address}
                        />
                    })
                }
            </div>
        )
    }
}

class QueryResult extends Component {
    render () {
        return (
            <div>
                {this.props.title}
                {this.props.address}
            </div>
        )
    }
}


export default Query;
