import React, { Component } from 'react';
import './Home.css';
import { BrowserRouter } from 'react-router-dom';

import Contents from './Contents';
import Query from './Query';

const tempValue = {
    "image": "https://placeimg.com/64/64/1"
}

class Home extends Component {
    state = {
        query: "",
        redirect: false
    }

    handleChange = (e) => {
        this.props.val = e.target.value
        // this.setState({
        //     query: e.target.value
        // })
    }

    handleKeyDown = (e) => {
        if (e.key === "Enter") {
            this.searchQeury(e);
        }
    }

    searchQeury = (e) => {
        alert(this.props.value);
        this.setState({
            redirect: true
        });
    }

    render() {
        let result;
        if (this.state.redirect) {
            result = <Query query={this.state.query}/>
        } else {
            result = <Contents/>
        }

        return (
            <BrowserRouter>
                <div>
                    <div class="home-title-area">
                        <a href="/" class="home-title">Date Mate</a>
                    </div>
                    <div class="home-search-bar">
                        <div class="home-search-icon" ><img class="icon-image" src={tempValue.image} alt="search-icon" /></div>
                        <input class="home-search-text" type="text"
                            value={this.state.query}
                            onChange={this.handleChange}
                            onKeyDown={this.handleKeyDown} />
                        <input class="home-search-button" type="button" value="검색" onClick={this.searchQeury} />
                    </div>
                    {result}
                </div>
            </BrowserRouter>
        )
    }
}

export default Home;
