import React, { Component } from 'react';
import './Query.css';

class Query extends Component {
    state = {
        word: "",
        completed: 0
    }

    componentDidUpdate() {
        this.getQuery()
        .then(res => {
            this.setState({word: res})
        })
        .catch(err => console.log(err));        
    }

    componentWillReceiveProps() {
        
    }

    componentDidMount() {        
        this.getQuery()
            .then(res => {
                this.setState({word: res})
            })
            .catch(err => console.log(err));        
    }

    getQuery = async() => {        
        const response = await fetch('/api/query?query=' + this.props.keyword);
        const body = await response.json();
        return body;
    }

    render() {
        return (
            <div className="query-contatiner">
                Query Page
                <br/>
                {this.state.word.query}
                <br/>
                {this.props.keyword}
            </div>
        )
    }
}

export default Query;
