import React, { Component } from 'react';
import './Home.css';
import { BrowserRouter, Redirect } from 'react-router-dom';

import Contents from './Contents';

const tempValue = {
    "image": "https://placeimg.com/64/64/1"
}

class Home extends Component {
    state = {
        query: ""
    }

    handleChange = (e) => {
        this.setState({
            query: e.target.value
        })
    }

    handleKeyDown = (e) => {
        if (e.key === "Enter") {
            this.searchQeury(e);
        }
    }

    searchQeury = (e) => {
        alert(this.state.query);
    }

    render() {
        return (
            <BrowserRouter>
                <div>
                    <div class="home-title-area">
                        <div class="home-title">
                        Date Mate
                        </div>
                        <Redirect path="*" to="/" class="home-title">
                            
                        </Redirect>
                    </div>
                    <div class="home-search-bar">
                        <div class="home-search-icon" ><img class="icon-image" src={tempValue.image} alt="search-icon" /></div>
                        <input class="home-search-text" type="text"
                            value={this.state.query}
                            onChange={this.handleChange}
                            onKeyDown={this.handleKeyDown} />
                        <input class="home-search-button" type="button" value="검색" onClick={this.searchQeury} />
                    </div>
                    <Contents />
                </div>
            </BrowserRouter>
        )
    }
}

export default Home;
