import React, { Component } from 'react';
import './Home.css';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';

import Contents from './Contents';
import Query from './Query';

const tempValue = {
    "image": "https://placeimg.com/64/64/1"
}

class Home extends Component {
    state = {
        query: "",
        redirect: false
    }

    handleChange = (e) => {
        this.setState({
            query: e.target.value
        })
    }

    handleKeyDown = (e) => {
        if (e.key === "Enter") {
            this.searchQeury(e);
        }
    }

    searchQeury = (e) => {
        this.setState({
            redirect: true
        });
    }

    render() {
        let result;
        if (this.state.redirect) {
            result = <Redirect from="*" to={{
                pathname: '/query',
                state: { val: this.state.query}
            }}/>
            //<Query query={this.state.query}/>
        } else {
            result = <Contents/>
        }

        return (
            <BrowserRouter>
                <div>
                    <div className="home-title-area">
                        <a href="/" className="home-title">Date Mate</a>
                    </div>
                    <div className="home-search-bar">
                        <div className="home-search-icon" ><img class="icon-image" src={tempValue.image} alt="search-icon" /></div>
                        <input className="home-search-text" type="text"
                            value={this.state.query}
                            onChange={this.handleChange}
                            onKeyDown={this.handleKeyDown} />
                        <input className="home-search-button" type="button" value="검색" onClick={this.searchQeury} />
                    </div>
                    {result}
                </div>
                <Switch>
                    <Route path="/query" component={Query} />
                </Switch>
            </BrowserRouter>
        )
    }
}

export default Home;
