import React, { Component } from 'react';
import './Weather.css';

class Weather extends Component{
    state = {
        Seoul:'',
        Busan:''
    }

    componentDidMount() {        
        this.getWeather('seoul').then(res => {
            this.setState({
                Seoul: res.temp
            });
        });
        this.getWeather('busan').then(res => {
            this.setState({
                Busan: res.temp
            });
        });
    }

    getWeather = async(city) => {
        const response = await fetch('/api/weather?city=' + city);
        const body = await response.json();
        return body;
    }

    render(){
        return(
            <div className="weather-container">
                <div className="weather">
                    <img class="map-image" src="resource/map.jpg" alt="map"/>
                    <span className="seoul">서울 기온 {this.state.Seoul}℃</span>
                    <span className="busan">부산 기온 {this.state.Busan}℃</span>
                </div>                
            </div>
        )
    }
};

export default Weather;
