import React, { Component } from 'react';
import './Home.css';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';

import Contents from './Contents';
import Query from './Query';

const tempValue = {
    "image": "https://placeimg.com/64/64/1"
}

class Home extends Component {
    state = {
        query: "",
        queryPath: "",
        redirect: false,
        isQuery: false
    }

    handleChange = (e) => {
        this.setState({
            query: e.target.value,
            queryPath: "/query/" + this.state.query
        })
    }

    handleKeyDown = (e) => {
        if (e.key === "Enter") {
            this.searchQeury(e);
        }
    }

    searchQeury = (e) => {
        this.setState({
            
            redirect: true,
            isQuery: true
        });

        alert(this.state.queryPath);
    }

    render() {
        let result;
        if (this.state.redirect) {
            result = <Redirect from="*" to={{
                pathname: '/query',
                state: {
                     query: this.state.query,
                }
            }}/>
            //<Query query={this.state.query}/>
        } else {
            result = <Contents/>
        }

        return (
            <BrowserRouter>
                <div>
                    <div className="home-title-area">
                        <a href="/" className="home-title">Date Mate</a>
                    </div>
                    <div className="home-search-bar">
                        <div className="home-search-icon" ><img class="icon-image" src={tempValue.image} alt="search-icon" /></div>
                        <input className="home-search-text" type="text"
                            value={this.state.query}
                            onChange={this.handleChange}
                            onKeyDown={this.handleKeyDown} />
                        {/* <span className="home-search-button" onClick={this.searchQeury}>검색</span> */}
                        <a href={this.state.queryPath}><span className="home-search-button">검색</span></a>
                    </div>
                    {result}
                </div>
                <Switch>
                    <Route path="/query"
                            render={(props) => <Query keyword={this.state.query}/>}/>
                </Switch>
            </BrowserRouter>
        )
    }
}

export default Home;
