            .catch(err => console.log(err));
    }

    getWeather = async() => {
        const response = await fetch('/api/weather');
        const body = await response.json();
        return body;
    }

    render(){
        return(
            <div className="weather-container">
                <div className="weather">
                    날씨 {this.state.data}
                </div>
                <div className="dust">
                    미세먼지
                </div>
            </div>
        )
    }
};

export default Weather;
