import React, { Component } from 'react';
import './Query.css';

class QueryResult extends Component {
    render () {
        return 
    }
}

class Query extends Component {
    state = {
        word: "",
        result: [
            {
                id: 0,
                title: "시발",
                address: "병신아"
            }
        ]

    }

    componentDidMount() {
        this.UpdateQuery();
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.props.keyword !== prevProps.keyword) {
            this.UpdateQuery();
        }
    }

    UpdateQuery = () => {
        this.getQuery()
            .then(res => {
                this.setState({
                    word: res.query
                })
            })
            .catch(err => console.log(err));
    }

    getQuery = async () => {
        const response = await fetch('/api/query?query=' + this.props.keyword);
        const body = await response.json();

        console.log(body);
        return body;
    }

    render() {
        return (
            <div className="query-contatiner">
                Query Page
                <br />
                {this.state.word}
                <br />
                {this.props.keyword}
            </div>
        )
    }
}

export default Query;
