import React, { Component } from 'react';
import './Weather.css';

class Weather extends Component{
    state = {
        Seoul:{
        },
        Busan:{
        }
    }

    componentDidMount() {        
        this.getWeather('seoul').then(res => {
            this.setState({
                Seoul: {
                    temp: res.temp,
                    weather: res.weather
                }
            });
        });
        this.getWeather('busan').then(res => {
            this.setState({
                Busan: {
                    temp: res.temp,
                    weather: res.weather
                }
            });
        });
    }

    getWeather = async(city) => {
        const response = await fetch('/api/weather?city=' + city);
        const body = await response.json();
        return body;
    }

    render(){
        return(
            <div className="weather-container">
                <div className="weather">
                    <img class="map-image" src="resource/map.jpg" alt="map"/>
                    <span className="seoul">서울 기온 {this.state.Seoul.temp}℃, 날씨 {this.state.Seoul.weather}</span>
                    <span className="busan">부산 기온 {this.state.Busan.temp}℃, 날씨 {this.state.Busan.weather}</span>
                </div>                
            </div>
        )
    }
};

export default Weather;
