import React, { Component } from 'react';
import './Place.css';

class Place extends Component{
    state = {
        results:[],
        place:''
    }

    componentDidMount() {
        this.getPlace()
            .then(result => {
                console.log(result.query);
                this.setState({
                    results: result.query,
                    place: result.place
                })
            })
    }

    getPlace = async () => {
        const response = await fetch('/api/place')
        const body = await response.json();

        return body;
    }

    render() {
        return (
            <div className="place-container">
                {
                    this.state.results.map(q => {
                        return (
                            <div className="place-container">
                                <div>
                                    <a href={"https://map.naver.com/v5/search/" + q.title} target="_blank" rel="noopener noreferrer">
                                        {q.title}
                                    </a>
                                </div>
                                <div>
                                    종류: {q.category}
                                </div>
                                <div>
                                    위치: {q.address}
                                </div>
                                <div>
                                    번호: {q.telephone}
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
};

export default Place;
