import React, { Component } from 'react';
import './Home.css';
import { BrowserRouter, Route, Switch, Link } from 'react-router-dom';

import Weather from './Weather';
import Place from './Place';

const tempValue = {
    "image": "https://placeimg.com/64/64/1"
}


class Home extends Component {
    state = {
        query: ""    
    }

    handleChange = (e) =>{
        this.setState({
            query: e.target.value
        })
    }

    searchQeury = (e) => {
        alert(this.state.query);
    }

    render() {
        return (
            <BrowserRouter>
                <div>
                    <div class="home-title-area">
                        <Link to="/" class="home-title">
                            Date Mate
                        </Link>
                    </div>
                    <div class="home-search-bar">
                        <div class="home-search-icon" ><img class="icon-image" src={tempValue.image} alt="search-icon" /></div>
                        <input class="home-search-text" type="text" value={this.state.query} />
                        <input class="home-search-button" type="button" value="검색" onClick={this.searchQeury}/>
                    </div>
                    <div class="home-menu">
                        <Link to="/weather" class="home-menu-weather">
                            실시간 기상
                        </Link>
                        <Link to="/place" class="home-menu-place">
                            추천 장소
                        </Link>
                    </div>
                </div>
                <Switch>
                    <Route path="/weather" component={Weather} />
                    <Route path="/place" component={Place} />
                </Switch>
            </BrowserRouter>
        )
    }
}

export default Home;
