import React, { Component } from 'react';
import './Weather.css';

class Weather extends Component{
    state = {
        Seoul:'',
        Busan:'',
        Gwangju:''
    }

    componentDidMount() {
        this.getWeather()
            .then(res => {
                console.log(res.data);
                this.setState({
                    Seoul: res.Seoul,
                    Busan: res.Busan,
                    Gwangju: res.Gwangju
                })                
            })
            .catch(err => console.log(err));
    }

    getWeather = async(city) => {
        const response = await fetch('/api/weather');
        const body = await response.json();
        return body;
    }

    render(){
        return(
            <div className="weather-container">
                <div className="weather">
                    <div>서울 기온 {this.state.Seoul}</div>
                    <div>부산 기온 {this.state.Busan}</div>
                    <div>광주 기온 {this.state.Gwangju}</div>
                </div>
                <div className="dust">
                    미세먼지
                </div>
            </div>
        )
    }
};

export default Weather;
